controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (i < 4) {
        i += 1
    } else {
        i = 0
    }
    if (Spectate == 1) {
        scaling.scaleToPercent(Spectator, list[i], ScaleDirection.Uniformly, ScaleAnchor.Middle)
    } else {
        if (ShieldStatus == 1) {
            if (Hero.vy == 0) {
                Hero.vy = -80
            }
            if (Hero.isHittingTile(CollisionDirection.Bottom)) {
                Hero.vy = -80
            } else if (canDoubleJump) {
                Hero.vy = -40
                canDoubleJump = false
            }
        } else {
            if (Hero.vy == 0) {
                Hero.vy = -80
            }
            if (Hero.isHittingTile(CollisionDirection.Bottom)) {
                Hero.vy = -120
            } else if (canDoubleJump) {
                Hero.vy = -100
                canDoubleJump = false
            }
        }
    }
})
